import React from "react";
import logoLogin from "../images/logo_Arx_Blanco.png";
import { Link } from "react-router-dom";

var link;

function sendMail() {
  var link =
    "mailto:perezalvareztomerick32@gmail.com" +
    "?cc=tpereza@unal.edu.co" +
    "&subject=" +
    escape("Prueba correo") +
    "&body=" +
    escape(document.getElementById("Prueba texto").value);
  window.location.href = link;
}

class InfoForm extends React.Component {
  handleClick = e => {
    sendMail();
  };
  handleSubmit = e => {
    e.preventDefault();
    console.log("Form was subimited");
    console.log(this.state);
  };

  render() {
    return (
      <div className="limiter">
        <div className="container-login100">
          <div className="wrap-login100 p-b-160 p-t-50">
            <form
              onSubmit={this.handleSubmit}
              className="login100-form validate-form"
            >
              <span className="login100-form-title">
                <Link to="/First">
                  <img src={logoLogin} alt="" />
                </Link>
              </span>

              <div
                className="wrap-input100 rs1 validate-input"
                data-validate="Username is required"
              >
                <input
                  className="input100 has-val"
                  type="text"
                  name="firstname"
                />
                <span className="label-input100">Nombre</span>
              </div>

              <div
                className="wrap-input100 rs2 validate-input"
                data-validate="Password is required"
              >
                <input
                  className="input100 has-val"
                  type="text"
                  name="lastname"
                />
                <span className="label-input100">Apellido</span>
              </div>

              <div className="wrap-input100-b validate-input">
                <input className="input100 has-val" type="text" name="email" />
                <span className="label-input100">Email</span>
              </div>

              <div className="wrap-input100-b validate-input">
                <select className="input100 has-val" name="genero">
                  <option />
                  <option>Hombre</option>
                  <option>Mujer</option>
                </select>
                <span className="label-input100">Género</span>
              </div>

              <div className="container-login100-form-btn">
                <button
                  onClick={this.handleClick}
                  className="login100-form-btn"
                >
                  Obtener Información
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default InfoForm;
